import React from 'react';
import { render } from 'react-dom';
import Spinner from 'react-spinkit';
import './Rand.css';
import getData  from './Data.js';
import { wait } from '@testing-library/dom';


class Rand extends React.Component {

  constructor(props) {
    super(props);
    this.handleClick = this.handleClick.bind(this);
    this.state = { random: "WELCOME TO TRUTH OR DARE RANDOMIZER" };
  }

  getRandomWithOneExclusion(lengthOfArray,indexToExclude){

    var rand = null;  //an integer
  
      while(rand === null || rand === indexToExclude){
         rand = Math.round(Math.random() * (lengthOfArray - 1));
      }
  
    return rand;
  }

  wait(ms){
    var start = new Date().getTime();
    var end = start;
    while(end < start + ms) {
      end = new Date().getTime();
   }
 }

  handleClick() {
    const min = 1;
    const max = 100;
    const rand = Math.round(min + Math.random() * (max - min));
    const data = getData();
    console.log(data);
    Promise.all(data).then(results =>{
        this.setState({ random: results[rand].message });
    })
   }

  render() {
    return (
      <div className="rand_container">
        <button className="rand_button"
         onClick={this.handleClick.bind(this)}>
         Start
        </button>
        <div className="rand_result">
            <div className="rand_message">
                The number is: {this.state.random}
            </div>
            <Spinner className="load" color="Yellow" name="pacman" />
        </div>
      </div>
    );
  }
}

export default Rand;
