import React from 'react';
import { render } from 'react-dom';
import Spinner from 'react-spinkit';
import './Rand.css';
import getData  from './Data.js';


class Rand extends React.Component {

  constructor(props) {
    super(props);
    this.handleClick = this.handleClick.bind(this);
    this.state = { type:"Hello",number:-1, message: "WELCOME TO TRUTH OR DARE RANDOMIZER" };
  }

  getRandomWithOneExclusion(lengthOfArray,indexToExclude){

    var rand = null;  //an integer
  
      while(rand === null || rand === indexToExclude){
         rand = Math.round(Math.random() * (lengthOfArray - 1));
      }
  
    return rand;
  }

  handleClick() {
    const data = getData();
    this.setState({ type: "load" });
    const fs = require('fs')
    fs.readFile('./randomed.json', 'utf8', (err, jsonString) => {
        if (err) {
            console.log("File read failed:", err)
            return
        }
        console.log('File data:', jsonString) 
    })
    setTimeout(() => {
        const min = 0;
        const max = 25;
        const rand = Math.round(min + Math.random() * (max - min));
        
        Promise.all(data).then(res =>{
            this.setState({ type:data[rand].key.toUpperCase() ,number: rand, message: data[rand].message });
        })   
    },2000);
   }

  render() {
    return (
      <div className="rand_container">
        <button className="rand_button"
         onClick={this.handleClick.bind(this)}>
         Start
        </button>
        <div className="rand_result">
            {this.state.type ==="load" ? (
                <Spinner className="load" color="Yellow" name="pacman" fadeIn="quarter" />
            ) : (
                <div className="rand_message">
                    {this.state.number  ===-1 ? (
                        console.log("lalala")
                    ) : (
                        <h3>{this.state.number+1}</h3>
                    )}
                    <h3>{this.state.type}</h3>
                    <p>{this.state.message}</p>
                </div>
            )}
        </div>
      </div>
    );
  }
}

export default Rand;
